import React, { Component } from 'react';
import { ListItem, ListItemText, List, Collapse } from '@material-ui/core';
import { ExpandLess, ExpandMore } from '@material-ui/icons';

function getItems() {
    var json = {
        list: [
            {
                id: 1,
                title: "What is Covid-19",
                items: [
                    {
                        id: 1,
                        name: "Course of Disease",
                        
                    },
                    {
                        id: 2,
                        name: "Origin"
                    },
                    {
                        id:3,
                        name: "Infection Prevention"
                    },
                    {
                        id:4,
                        name: "Myths and Misconceptions"
                    },
                    {
                        id:5,
                        name: "Sterotypes and Stigma"
                    },
                    {
                        id:6,
                        name: "Hygiene Tips"
                    },
                    {
                        id:7,
                        name: "Guidelines to follow"
                    },
                    {
                        id:8,
                        name: "UNESCO's initiatives"
                    },  
                ]
            },
            {
                id: 2,
                title: "Mental Health",
                items: [
                    {
                        id: 1,
                        name: "Defining Mental health"
                    },
                    {
                        id: 2,
                        name: "Importance of Mental health",
                    },
                    {
                        id: 3,
                        name: "Different Mental health Problem",
                    },
                    {
                        id: 4,
                        name: "Reasons for Stress",
                    },
                    {
                        id: 5,
                        name: "Stress Vs Depression",
                    }
                ]
            },
            {
                id: 3,
                title: "Module-3",
                items: [
                    {
                        id: 1,
                        name: "Stress Due To Self-isolation"
                    },
                    {
                        id: 2,
                        name: "Routine During Lockdown"
                    },
                    {
                        id: 3,
                        name: "Staying away from elderly"
                    },
                    {
                        id: 4,
                        name: "Dealing with the problem"
                    }
                ]
            }, 
            {
                id:4,
                title: "Module-4",
                items: [
                    {
                        id:1,
                        name: "How to Support and interact with Community?"
                    },
                    {
                        id:2,
                        name: "Reliable source For COVID News"
                    },
                    {
                        id:3,
                        name:"How can parents talk to their children about Covid-19?"
                    },
                    {
                        id:4,
                        name: "How can teacher talk to their students about Covid-19?"
                    }
                ]
            }
        ]
    };
    return json;
}
class NestedList extends Component {
    state = {};
    handleClick = e => {
        this.setState({ [e] : !this.state[e]});
    };
    render(){
    const items = getItems();
        return(
        <div>
            {items.list.map(list => {
                    return(
                    <div key={list.id}>
                        <ListItem
                            key={list.id}
                            onClick = {this.handleClick.bind(
                                this,
                                list.name
                            )}
                        > 
                        <ListItemText 
                            primary={list.name}    
                        />
                        {this.state[list.name] ? (
                            <ExpandLess/>
                        ):(
                            <ExpandMore/>
                        )}
                        </ListItem>
                        <Collapse
                          key={list.items.id}
                          component="li"
                          in={this.state[items.name]}
                          timeout="auto"
                          unmountOnExit
                        >
                            <List disablePadding>
                                {list.items.map(
                                    subitem => {
                                    return(
                                        <ListItem
                                            button
                                            key={subitem.id}
                                        >
                                            <ListItemText
                                              key={
                                                  subitem.id
                                              }
                                              primary={
                                                  subitem.name
                                              }
                                            >    
                                            </ListItemText>
                                        </ListItem>
                                    )
                                    }
                                )}
                                </List>
                        </Collapse>
                    </div>
                    )
                })}
        </div>
        )
        
    }
}

export default NestedList;
